package com.example.hom1mon4.ui.onBoarding.BoardingAdapter

import android.view.LayoutInflater
import android.view.ViewGroup
import androidx.core.view.isVisible
import androidx.recyclerview.widget.RecyclerView
import com.example.hom1mon4.databinding.ItemOnboardingBinding
import com.example.hom1mon4.ui.OnBoard.OnBoard
import com.example.hom1mon4.ui.utils.loadIMage

class OnBoardingAdapter(private val onClick:() -> Unit): RecyclerView.Adapter<OnBoardingAdapter.OnBoardingViewHolder>() {
    private val data = arrayListOf(
        OnBoard("расслабся и наслаждайся", "приложение для отслеживание прибытие в игре", "data:image/jpeg;base64,"),
        OnBoard("Время для игр", "Играй с друзьями по сети","https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQx2pc3yym4rWaMDqnL_D_57oLa4FcANR0U3FcyNxZunm3TOHakZDoxEmjGnU66u0lo5KE&usqp=CAU" ),
        OnBoard("Играй по сети или оффлай","С миллионами игроков!","https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRfJwbzXOINP8KeIwoUEHhTzqfKmQq-yKvAug&usqp=CAU")


    )
    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): OnBoardingViewHolder {
        return OnBoardingViewHolder(ItemOnboardingBinding.inflate(LayoutInflater.from(parent.context),parent,false))
    }


    override fun onBindViewHolder(holder: OnBoardingViewHolder, position: Int) {
        holder.bind(data[position])
    }

    override fun getItemCount(): Int {
        return data.size
    }

    inner class OnBoardingViewHolder(private val binding: ItemOnboardingBinding): RecyclerView.ViewHolder(binding.root){
        fun bind(onBoard: OnBoard) {
            binding.boardTask.text = onBoard.title
            binding.descText.text = onBoard.decs
            binding.boardImg.loadIMage(onBoard.image.toString())
            binding.btnStartApp.isVisible = adapterPosition == data.lastIndex
            binding.btnStartApp.setOnClickListener{
                onClick()
            }
        }

    }


}